import json
from datetime import datetime

rpc = 'https://rpc.ankr.com/bsc'

start_time = datetime.now()

with open('addresses.txt', 'r') as file:
    addresses = [row.strip() for row in file]

web3 = Web3(HTTPProvider(rpc))

