busd_contract = web3.eth.contract('0xe9e7CEA3DedcA5984780Bafc599bD69ADd087D56', abi=abi)
decimals = busd_contract.functions.decimals().call()

wallets_with_balance = {}

for address in addresses:
    address = web3.to_checksum_address(address)
    amount_wei = busd_contract.functions.balanceOf(address).call()
    amount = round(amount_wei / 10**decimals, 3)

    if amount > 0.4:
        wallets_with_balance[address] = amount

with open('wallets_with_balance.txt', 'w') as file:
    for addr, value in wallets_with_balance.items():
        file.write(f'{addr}: {value} BUSD\n')

end_time = datetime.now()
elapsed_time = end_time - start_time

print(f'elapsed_time: {elapsed_time.seconds} seconds')
